import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Table.css';

function EmployeeList() {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/empleados');
        console.log('Datos de empleados recibidos:', response.data);
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  // Definimos el orden de las columnas que queremos mostrar
  const columnOrder = ['id_emp', 'Nombres', 'Apellidos', 'DNI', 'Email', 'estado', 'contrato', 'RUC'];

  return (
    <div>
      <h2>Lista de Empleados</h2>
      <table>
        <thead>
          <tr>
            {columnOrder.map(key => (
              <th key={key}>{key}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {employees.map(employee => (
            <tr key={employee.id}>
              {columnOrder.map(key => (
                <td key={key}>{employee[key]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EmployeeList;
