import React, { useState } from 'react';
import './App.css';
import Sidebar from './components/Sidebar';
import EmployeeList from './components/EmployeeList';
import PayrollList from './components/PayrollList';
import FondoPensionList from './components/FondoPensionList';

function App() {
  const [selectedOption, setSelectedOption] = useState('');

  const renderContent = () => {
    switch(selectedOption) {
      case 'Listado de empleados':
        return <EmployeeList />;
      case 'Generacion de nomina':
        return <PayrollList />;
      case 'Fondos de Pension':
        return <FondoPensionList />;
      default:
        return <div>Selecciona una opción del menú</div>;
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <img src="/logo.png" alt="Logo" className="app-logo" />
        <h1 className="app-title">Sistema de Planillas - Proinnovate</h1>
      </header>
      <div className="app-content">
        <Sidebar setSelectedOption={setSelectedOption} />
        <main className="main-content">
          {renderContent()}
        </main>
      </div>
    </div>
  );
}

export default App;
