import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CrearPlanillaMensual() {
  const [tiposPlanilla, setTiposPlanilla] = useState([]);
  const [selectedTipo, setSelectedTipo] = useState('');
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedMonth, setSelectedMonth] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Cargar tipos de planilla desde la API
    setIsLoading(true);
    setError(null);
    axios.get('/api/tipos-planilla')
      .then(response => {
        setTiposPlanilla(response.data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error al cargar tipos de planilla:', error);
        setError('Error al cargar los tipos de planilla. Por favor, intente de nuevo más tarde.');
        setIsLoading(false);
      });
  }, []);

  const handleGenerar = async () => {
    setIsLoading(true);
    setError(null);
    setMessage('');

    try {
      const response = await axios.post('/api/planilla-mensual/crear', {
        año: selectedYear,
        mes: selectedMonth
      });
      setMessage(response.data.message);
    } catch (error) {
      console.error('Error al generar la planilla:', error);
      setError('Error al generar la planilla. Por favor, intente de nuevo más tarde.');
    }

    setIsLoading(false);
  };

  const years = Array.from({ length: 10 }, (_, i) => new Date().getFullYear() - i);
  const months = [
    'Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
    'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
  ];

  if (isLoading) {
    return <div>Cargando...</div>;
  }

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  return (
    <div>
      <h2>Generación de Planilla Mensual</h2>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <select 
          value={selectedTipo} 
          onChange={(e) => setSelectedTipo(e.target.value)}
          style={{ width: '30%' }}
        >
          <option value="">Seleccione tipo de planilla</option>
          {tiposPlanilla.map(tipo => (
            <option key={tipo.ID_tipo} value={tipo.ID_tipo}>{tipo.nombre}</option>
          ))}
        </select>
        <select 
          value={selectedYear} 
          onChange={(e) => setSelectedYear(e.target.value)}
          style={{ width: '30%' }}
        >
          <option value="">Seleccione año</option>
          {years.map(year => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>
        <select 
          value={selectedMonth} 
          onChange={(e) => setSelectedMonth(e.target.value)}
          style={{ width: '30%' }}
        >
          <option value="">Seleccione mes</option>
          {months.map((month, index) => (
            <option key={index} value={index + 1}>{month}</option>
          ))}
        </select>
      </div>
      <button onClick={handleGenerar} disabled={!selectedTipo || !selectedYear || !selectedMonth}>
        Generar
      </button>
      {message && <div style={{ color: 'green', marginTop: '10px' }}>{message}</div>}
    </div>
  );
}

export default CrearPlanillaMensual;
