import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Table.css';

function PayrollList() {
  const [payrolls, setPayrolls] = useState([]);

  useEffect(() => {
    const fetchPayrolls = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/nominas');
        console.log('Datos de nóminas recibidos:', response.data);
        setPayrolls(response.data);
      } catch (error) {
        console.error('Error fetching payrolls:', error);
      }
    };

    fetchPayrolls();
  }, []);

  // Definimos el orden de las columnas que queremos mostrar para las nóminas
  // Ajusta esto según los campos reales de tu tabla de nóminas
  const columnOrder = ['id_salario', 'monto'];

  return (
    <div>
      <h2>Lista de Nóminas</h2>
      <table>
        <thead>
          <tr>
            {columnOrder.map(key => (
              <th key={key}>{key}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {payrolls.map(payroll => (
            <tr key={payroll.id}>
              {columnOrder.map(key => (
                <td key={key}>{payroll[key]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PayrollList;
