import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './Table.css';

function BasicoTab() {
  const [payrolls, setPayrolls] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
  const [columns, setColumns] = useState([
    { key: 'id_salario', label: 'ID', width: 100 },
    { key: 'Base', label: 'Monto Básico', width: 150 },
    // Añade más columnas según sea necesario
  ]);

  useEffect(() => {
    const fetchPayrolls = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/nominas');
        setPayrolls(response.data);
      } catch (error) {
        console.error('Error fetching payrolls:', error);
      }
    };

    fetchPayrolls();
  }, []);

  const requestSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  const sortedPayrolls = React.useMemo(() => {
    let sortablePayrolls = [...payrolls];
    if (sortConfig.key !== null) {
      sortablePayrolls.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }
    return sortablePayrolls;
  }, [payrolls, sortConfig]);

  const handleResize = useCallback((index, newWidth) => {
    setColumns(prevColumns => {
      const newColumns = [...prevColumns];
      newColumns[index] = { ...newColumns[index], width: newWidth };
      return newColumns;
    });
  }, []);

  return (
    <div className="table-container">
      <h2>Listado de Nóminas</h2>
      <table className="resizable-table">
        <thead>
          <tr>
            {columns.map((column, index) => (
              <th 
                key={column.key} 
                onClick={() => requestSort(column.key)}
                style={{ width: `${column.width}px`, minWidth: `${column.width}px` }}
              >
                {column.label}
                {sortConfig.key === column.key && (
                  <span>{sortConfig.direction === 'ascending' ? ' ▲' : ' ▼'}</span>
                )}
                <div
                  className="resizer"
                  onMouseDown={(e) => {
                    const startX = e.pageX;
                    const startWidth = column.width;
                    const handleMouseMove = (e) => {
                      handleResize(index, Math.max(50, startWidth + e.pageX - startX));
                    };
                    document.addEventListener('mousemove', handleMouseMove);
                    document.addEventListener('mouseup', () => {
                      document.removeEventListener('mousemove', handleMouseMove);
                    }, { once: true });
                  }}
                />
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {sortedPayrolls.map(payroll => (
            <tr key={payroll.id}>
              {columns.map(column => (
                <td key={column.key} style={{ width: `${column.width}px`, minWidth: `${column.width}px` }}>
                  {payroll[column.key]}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default BasicoTab;
